import React, { useState } from "react";
import classes from './Products.module.css'
import Card from "../../components/product_card/ProductCard";
import { Fade } from "react-awesome-reveal";
import Modal from "../../components/modal/Modal";

let test_card = ["../../../public/images/image13.png", ["Фасад (“Бифлекс” и  “Масло”)", "Размер 200x110 см (разложенный) Три варианта расцветок: белый, коричневый, серый (антрацит)"], '5200']

const Products = () => {

    const [isModalActive, setModalActive] = useState(false);

    const handleModalOpen = () => {
        setModalActive(true);
    };
    const handleModalClose = () => {
        setModalActive(false);
    };

    const [form, setForm] = useState({
        product_name: '',
        product_info: '',
        product_price: ''
    })

    const changeHandler = (event: React.FormEvent<HTMLInputElement> | React.ChangeEvent<HTMLTextAreaElement>) => {
        setForm({ ...form, [event.currentTarget.name]: event.currentTarget.value })
    }

    const onSubmit = async (event: any) => {

    }

    return (
        <div className={classes.page_container}>
            <div className={classes.page_head}>
                <h2 className="products_head" >Наши товары</h2>
            </div>
            <div className={classes.page_content}>
                <Fade triggerOnce cascade>
                    <Card photo={test_card[0]} info={test_card[1]} price={test_card[2]} />
                    <Card photo={test_card[0]} info={test_card[1]} price={test_card[2]} />
                    <Card photo={test_card[0]} info={test_card[1]} price={test_card[2]} />
                </Fade>
                {isModalActive && (
                    <Modal title="Войти в аккаунт" onClose={handleModalClose}>
                        <form onSubmit={onSubmit} method="get" className={classes.add_product__form}>
                            <div className={classes.add_product_form}>
                                <label > Название товара: </label>
                                <input
                                    onChange={changeHandler}
                                    type="text"
                                    name="product_name"
                                    id="product_name"
                                    required
                                />
                            </div>
                            <div className={classes.add_product_form}>
                                <label> Информация о товаре: </label>
                                <input
                                    onChange={changeHandler}
                                    type="text"
                                    name="product_info"
                                    id="product_info"
                                    required
                                />

                            </div>
                            <div className={classes.add_product_form}>
                                <label> Цена товара: </label>
                                <input
                                    onChange={changeHandler}
                                    type="text"
                                    name="product_price"
                                    id="product_price"
                                    required
                                />

                            </div>
                            <div className={classes.add_product_form_button}>
                                <input type="button" value="Добавить" onClick={onSubmit} />
                            </div>
                        </form>
                    </Modal>
                )}
            </div>

            {window.location.pathname === '/admin' &&
                (
                    <button className={classes.add_product_button} type="button" onClick={handleModalOpen}>
                        Добавить товар
                    </button>
                )}
        </div>
    );
}

export default Products;
