import React from 'react';
import './index.css'
import Header from './components/header/Header';
import Footer from './components/footer/Footer';
import Main from './pages/main/Main';
import About from './pages/about/About';
import Products from './pages/products/Products';
import Contact from './pages/contact/Contact';
import Location from './pages/location/Location';
import Feedback from './pages/feedback/Feedback';
import { Animation, ScrollContainer, ScrollPage, Animator, batch, Sticky } from "react-scroll-motion";

const Spin = (cycle: number) =>
({
  in: {
    style: {
      // `p` is number (0~1)
      // When just before this page appear, `p` will be 0
      // When this page filled your screen, `p` will be 1
      transform: (p) => `rotate(${p * 360 * cycle}deg)`,
    },
  },
  out: {
    style: {
      // `p` is number (0~1)
      // When this page filled your screen, `p` will be 0
      // When just after this page disappear, `p` will be 1
      transform: (p) => `rotate(${p * 360 * cycle}deg)`,
    },
  },
} as Animation);


function App() {
  return (
    <div className="App">
      <Header />
      <main>
        <ScrollContainer>
          <ScrollPage>
    // Your custom animation also can be batched!
            <Animator animation={batch(Sticky(), Fade(), Spin(3))}>
              <h1 style={{ fontSize: 50 }}>Hello!!!</h1>
            </Animator>
          </ScrollPage>
          <ScrollPage>
            ...
          </ScrollPage>
        </ScrollContainer>
        <Main />
        <About />
        <Products />
        <Contact />
        <Location />
        <Feedback />
      </main>
      <Footer />
    </div>

  );
}

export default App;
